import axios from 'axios';
import React, { useEffect, useState } from 'react'

function App() {

  const [cryptoMoneyList, setCryptoMoneyList] = useState([]);
  const [searchText, setSearchText] = useState("")

  const getCrypto = async () => {
    axios.get("https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=100&page=1&sparkline=false")
      .then(response => {
        setCryptoMoneyList(response.data);
      })
  }

  useEffect(() => {

    getCrypto();

    return () => {
      setCryptoMoneyList([]);
    }

  }, []);

  useEffect(() => {

    if (searchText === "") {
      getCrypto();
    } else {
      const cryptos = cryptoMoneyList.filter(crypto => crypto.Name.includes(searchText));
      console.log(cryptos);
    }

    return () => {
      setCryptoMoneyList([]);
    }

  }, [searchText])

  return (
    <div className='grid gap-10 w-full h-screen bg-white p-2'>
      <div className='flex w-full h-max items-center justify-center'>
        <input
          value={searchText}
          placeholder="Search"
          onChange={e => setSearchText(e.target.value)}
          className="w-96 h-max p-2 border border-black rounded-lg focus:ring-blue-500"
        />
      </div>

      <div className='w-full h-full overflow-auto'>

      </div>
    </div>
  )
}

export default App
